import React from 'react';
import axios from "axios";
import {Container, ListGroup,ListGroupItem,Button} from 'reactstrap';
import {CSSTransition,TransitionGroup} from 'react-transition-group';
import uuid from 'uuid';
export default class ShoppingList extends React.Component{
	state={
		items:[
		{id: uuid()},
		]
	}
  componentDidMount() {
    this.getDataFromDb();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 2000);
      this.setState({ intervalIsSet: interval });
    }
  }	

    componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
    }
  }

  getDataFromDb = () => {
    fetch("http://localhost:5000/get")
      .then(data => data.json())
  };

  postDataToDb =(name)=>{
  	      let currentIds=this.state.items.map(items=>items.id);
      let idToBeAdded=0;
      while(currentIds.includes(idToBeAdded)){
        ++idToBeAdded;
      }
  	axios.post("http://localhost:5000/post",{
  		id:idToBeAdded,
  		name:name
  	});
  };

  deleteData=(id)=>{
  	axios.delete("http://localhost:5000/delete",{
  		      data: {
        id: id
      }
  	})
  }

	render(){
		const { items,id } = this.state;
		for(let i=0;i<items.length;i++)
		return(
			<Container>
				<Button
				color="dark"
				style={{marginBottom:'2rem'}}
				onClick={() => {
					const name = prompt('Enter Item');
					if(name){
						this.postDataToDb(name);
					}
				}}
				>
				Add Item
				</Button>

				<ListGroup>
					<TransitionGroup className="shopping-list">
					{items.map(({id,name})=>(
						<CSSTransition key={id} timeout={500} classNames="fade">
					<ListGroupItem>
					<Button
					className="remove-btn"
					color="danger"
					size="sm"
					onClick={()=>this.deleteData(id)}
					>
					&times;
					</Button>
					{name}
					</ListGroupItem>
					</CSSTransition>
					))} 
					</TransitionGroup>
				</ListGroup>
			</Container>
			);
	}
}
